import React, { useState } from 'react';

const FormularioDiagnostico = () => {
  const [inputs, setInputs] = useState({ H2: '', CH4: '', C2H2: '', C2H4: '', C2H6: '' });
  const [resultado, setResultado] = useState(null);

  const handleChange = e => {
    setInputs({ ...inputs, [e.target.name]: e.target.value });
  };

  const diagnosticar = async e => {
    e.preventDefault();
    const res = await fetch('http://localhost:8000/diagnostico', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        H2: parseFloat(inputs.H2),
        CH4: parseFloat(inputs.CH4),
        C2H2: parseFloat(inputs.C2H2),
        C2H4: parseFloat(inputs.C2H4),
        C2H6: parseFloat(inputs.C2H6)
      })
    });
    const data = await res.json();
    setResultado(data);
  };

  return (
    <div style={{
      background: '#fff', padding: '2rem', borderRadius: '10px', boxShadow: '0 0 10px rgba(0,0,0,0.1)',
      maxWidth: '500px', margin: 'auto', marginTop: '3rem'
    }}>
      <h2 style={{ textAlign: 'center', color: '#004466' }}>Diagnóstico de Transformador</h2>
      <form onSubmit={diagnosticar}>
        {['H2', 'CH4', 'C2H2', 'C2H4', 'C2H6'].map(gas => (
          <div key={gas} style={{ marginBottom: '1rem' }}>
            <label>{gas} (ppm):</label>
            <input
              type="number"
              name={gas}
              value={inputs[gas]}
              onChange={handleChange}
              required
              style={{ width: '100%', padding: '0.5rem', borderRadius: '5px' }}
            />
          </div>
        ))}
        <button type="submit" style={{
          background: '#004466', color: '#fff', padding: '0.7rem 2rem',
          border: 'none', borderRadius: '6px', fontSize: '16px', width: '100%'
        }}>
          Diagnosticar
        </button>
      </form>

      {resultado && (
        <div style={{
          marginTop: '1.5rem', background: '#eaf8ff', padding: '1rem',
          borderLeft: '5px solid #00aaff', borderRadius: '8px'
        }}>
          <h4>Falha identificada:</h4>
          <p><strong>{resultado.falha}</strong></p>
        </div>
      )}
    </div>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 24,
    backgroundColor: '#eaeaea',
  },
  title: {
    marginTop: 16,
    paddingVertical: 8,
    borderWidth: 4,
    borderColor: '#20232a',
    borderRadius: 6,
    backgroundColor: '#61dafb',
    color: '#20232a',
    textAlign: 'center',
    fontSize: 30,
    fontWeight: 'bold',
  },
});

export default FormularioDiagnostico;
